import Sticky from '@/components/Sticky';
import Swiper from 'swiper';
import { getInfo } from '@/api/case';
import { designStyles, cdnHost, homeOptions, caseAlbumTypes } from '@/api/utils';
export default {
  components: {
    Sticky: Sticky
  },
  data: function data() {
    return {
      model: null,
      cdnHost: cdnHost,
      caseAlbumTypes: caseAlbumTypes,
      nav: null
    };
  },
  mounted: function mounted() {
    var _this = this;

    var interval = setInterval(function () {
      if (!_this.nav) {
        _this.nav = _this.$store.getters.activeNav;
      } else {
        clearInterval(interval);
      }
    }, 10);
    this.get();
  },
  methods: {
    get: function get() {
      var _this2 = this;

      var id = this.$route.params && this.$route.params.id;
      getInfo({
        id: id
      }).then(function (res) {
        if (res.code === 200) {
          _this2.model = res.data;
          _this2.model.albums = {};

          if (res.data.album && res.data.album.length > 0) {
            res.data.album.forEach(function (element) {
              if (!_this2.model.albums[element.type] || _this2.model.albums[element.type].length === 0) {
                _this2.$set(_this2.model.albums, element.type, []);
              }

              _this2.model.albums[element.type].push({
                name: element.url,
                url: cdnHost + element.url
              });
            });
          }

          _this2.$nextTick(function () {
            new Swiper('.swiper-nCaseShow', {
              slidesPerView: 1,
              loop: true,
              autoplay: true
            });
          });
        }
      });
    },
    getType: function getType(value) {
      var type = '';
      homeOptions.forEach(function (element) {
        if (element.value === value) {
          type = element.label;
        }
      });
      return type;
    },
    getDesignStyle: function getDesignStyle(value) {
      var style = '';
      designStyles.forEach(function (element) {
        if (element.value === value) {
          style = element.label;
        }
      });
      return style;
    }
  }
};
