import Swiper from "swiper";
import { getFrontNewsList } from "@/api/news";
import { cdnHost } from "@/api/utils";
export default {
  data: function data() {
    return {
      list: [],
      cdnHost: cdnHost
    };
  },
  mounted: function mounted() {
    var _this = this;

    getFrontNewsList({
      type: 7
    }).then(function (res) {
      if (res.code === 200) {
        _this.list = res.data;

        _this.$nextTick(function () {
          new Swiper(".swiper-banner", {
            slidesPerView: 1,
            loop: true,
            autoplay: true,
            pagination: {
              el: ".swiper-banner-pagination",
              clickable: true
            },
            navigation: {
              nextEl: ".swiper-banner-next",
              prevEl: ".swiper-banner-prev"
            }
          });
        });
      }
    });
  }
};
