import request from '@/utils/request';
import { host } from "./utils";
export function createNews(data) {
  return request({
    url: host + '/api/news/add',
    method: 'post',
    data: data
  });
}
export function getNewsList(params) {
  return request({
    url: host + '/api/news/list',
    method: 'get',
    params: params
  });
}
export function getFrontNewsList(params) {
  return request({
    url: host + '/api/front/news/list',
    method: 'get',
    params: params
  });
}
export function getNewsIndexList(params) {
  return request({
    url: host + '/api/news/index_list',
    method: 'get',
    params: params
  });
}
export function getNewsPage(page, params) {
  return request({
    url: host + '/api/front/news/page/' + page,
    method: 'get',
    params: params
  });
}
export function getNews(params) {
  return request({
    url: host + '/api/news/get',
    method: 'get',
    params: params
  });
}
export function getInfo(params) {
  return request({
    url: host + '/api/news/info',
    method: 'get',
    params: params
  });
}
export function deleteNews(params) {
  return request({
    url: host + '/api/news/delete',
    method: 'get',
    params: params
  });
}
