import '@/assets/web/css/main.css';
import { getFrontNewsList } from '@/api/news';
export default {
  name: 'PageLayout',
  data: function data() {
    return {
      list: [],
      path: '',
      pageHeight: 400
    };
  },
  computed: {
    key: function key() {
      return this.$route.path;
    }
  },
  created: function created() {
    var _this = this;

    if (this._isMobile()) {
      location.href = 'http://m.meiqiaozhuangshi.com';
    }

    if (!this.bindResize) {
      window.addEventListener("resize", this.calculateSize);
      this.bindResize = true;
    }

    var splitPath = this.$route.path.split('/');
    this.path = this.$route.path;

    if (splitPath.length > 2) {
      this.path = '/' + splitPath[1];
    }

    getFrontNewsList({
      type: 6
    }).then(function (res) {
      if (res.code === 200) {
        _this.list = res.data;

        _this.$store.dispatch('nav/storeNavs', _this.list);

        _this.list.forEach(function (e) {
          if (e.author === _this.path) {
            _this.$store.dispatch('nav/storeActiveNav', e);
          }
        }); // console.log(this.$store.getters.navs)

      }
    });
  },
  mounted: function mounted() {
    this.calculateSize();
  },
  methods: {
    _isMobile: function _isMobile() {
      var flag = navigator.userAgent.match(/(phone|pad|pod|iPhone|iPod|ios|iPad|Android|Mobile|BlackBerry|IEMobile|MQQBrowser|JUC|Fennec|wOSBrowser|BrowserNG|WebOS|Symbian|Windows Phone)/i);
      return flag;
    },
    calculateSize: function calculateSize() {
      this.pageHeight = window.innerHeight;
    }
  }
};
