import { qiniuHost, cdnHost, homeOptions, designStyles } from '@/api/utils';
import { getToken, uploadFile, deleteFile } from '@/api/qiniu';
import { getTeamList } from '@/api/team.js';
import { createPano, getPano } from '@/api/pano';
import moment from 'moment';
export default {
  props: {
    isEdit: {
      type: Boolean,
      default: false
    }
  },
  data: function data() {
    return {
      postForm: {
        name: '',
        type: '',
        pic: '',
        is_home: false,
        area: '',
        update_time: moment().format('YYYY-MM-DD HH:mm:ss'),
        design_style_id: '',
        status: '',
        designer: '',
        url: ''
      },
      rules: {
        name: [{
          required: true,
          message: '请填写名称',
          trigger: 'blur'
        }],
        type: [{
          required: true,
          message: '请选择户型',
          trigger: 'blur'
        }],
        pic: [{
          required: true,
          message: '请上传标题图片',
          trigger: 'blur'
        }],
        area: [{
          required: true,
          message: '请填写面积',
          trigger: 'blur'
        }],
        url: [{
          required: true,
          message: '请填写全景URL',
          trigger: 'blur'
        }],
        design_style_id: [{
          required: true,
          message: '请选择设计风格',
          trigger: 'blur'
        }],
        designer: [{
          required: true,
          message: '请选择设计师',
          trigger: 'blur'
        }],
        update_time: [{
          required: true,
          message: '请选择更新时间',
          trigger: 'blur'
        }]
      },
      homeOptions: homeOptions,
      qiniuHost: qiniuHost,
      loading: false,
      designerOptions: [],
      designStyles: designStyles,
      preview_image: '',
      cdnHost: cdnHost
    };
  },
  created: function created() {
    var _this = this;

    if (this.isEdit) {
      var id = this.$route.params && this.$route.params.id;
      getPano({
        id: id
      }).then(function (res) {
        if (res.code === 200) {
          _this.postForm = res.data;
        } else {
          _this.$alert(res.msg, '提示！', {
            confirmButtonText: '我知道啦！',
            type: 'warning'
          });

          _this.returnToList(0);
        }
      });
    } // 加载团队成员


    getTeamList().then(function (res) {
      if (res.code === 200) {
        res.data.forEach(function (element) {
          if (element.type === 0) {
            _this.designerOptions.push({
              id: element.id,
              name: element.name
            });
          }
        });
      }
    });
  },
  methods: {
    uploadPIC: function uploadPIC(request) {
      var _this2 = this;

      getToken().then(function (data) {
        var formData = new FormData();
        formData.append('token', data.qiniu_token);
        formData.append('key', 'Pano/' + moment().format('x'));
        formData.append('file', request.file);
        uploadFile(formData).then(function (response) {
          _this2.postForm.pic = response.key;
        });
      });
    },
    exceed: function exceed() {
      this.$alert('请先删除已上传的照片！', '每个全景标题图只能上传一张哦！', {
        confirmButtonText: '我知道啦！',
        type: 'warning'
      });
    },
    deletePic: function deletePic(file) {
      var _this3 = this;

      this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        deleteFile({
          key: file
        }).then(function (res) {
          if (res.code === 200 || res.code === 612) {
            _this3.$message({
              type: 'success',
              message: '删除成功!'
            });

            _this3.postForm.pic = '';
            return true;
          } else {
            _this3.$message({
              type: 'error',
              message: '删除失败!'
            });

            return false;
          }
        });
        return true;
      }).catch(function () {
        return false;
      });
      return false;
    },
    submitForm: function submitForm() {
      this.postForm.status = 1;
      this.submit();
    },
    draftForm: function draftForm() {
      this.postForm.status = 0;
      this.submit();
    },
    submit: function submit() {
      var _this4 = this;

      this.$refs.postForm.validate(function (valid) {
        if (valid) {
          _this4.loading = true;
          createPano(_this4.postForm).then(function (res) {
            _this4.loading = false;

            if (res.code === 200) {
              _this4.$notify({
                title: '成功',
                message: '全景操作成功',
                type: 'success',
                duration: 2000
              });

              if (_this4.isEdit) {
                _this4.returnToList();

                return;
              }

              _this4.$refs['postForm'].resetFields();

              for (var type in _this4.postForm.albums) {
                _this4.$delete(_this4.postForm.albums, type);
              }

              _this4.postForm.update_time = moment().format('YYYY-MM-DD HH:mm:ss');
            }
          });
        }
      });
    },
    returnToList: function returnToList() {
      var _this5 = this;

      this.$store.dispatch('tagsView/delView', this.$router.history.current).then(function (_ref) {
        var visitedViews = _ref.visitedViews;
        visitedViews.slice(-1)[0];

        _this5.$router.push('/admin/panorama/list');
      });
    }
  }
};
