import Summernote from '@/components/Summernote';
export default {
  components: {
    Summernote: Summernote
  },
  props: {
    isEdit: {
      type: Boolean,
      default: false
    }
  },
  data: function data() {
    return {
      postForm: {
        title: '',
        category: 5,
        description: '',
        is_home: false,
        content: '',
        update_time: moment().format('YYYY-MM-DD HH:mm:ss'),
        pic: '',
        status: '',
        view_count: '',
        author: '舒适100装饰',
        sort: 0
      },
      rules: {
        title: [{
          required: true,
          message: '请填写标题',
          trigger: 'blur'
        }],
        category: [{
          required: true,
          message: '请选择分类',
          trigger: 'blur'
        }],
        pic: [{
          required: false,
          message: '请上传标题图片',
          trigger: 'blur'
        }],
        description: [{
          required: false,
          message: '请填写描述',
          trigger: 'blur'
        }],
        content: [{
          required: true,
          message: '请填写新闻内容',
          trigger: 'blur'
        }],
        view_count: [{
          required: true,
