          trigger: 'blur'
        }],
        author: [{
          required: true,
          message: '请填写来源',
          trigger: 'blur'
        }],
        sort: [{
          required: true,
          message: '请填写排序',
          trigger: 'blur'
        }],
        update_time: [{
          required: true,
          message: '请选择更新时间',
          trigger: 'blur'
        }]
      },
      newsCategories: newsCategories,
      qiniuHost: qiniuHost,
      loading: false,
      preview_image: '',
      cdnHost: cdnHost
    };
  },
  created: function created() {
    var _this = this;

    if (this.isEdit) {
      var id = this.$route.params && this.$route.params.id;
      getNews({
        id: id
      }).then(function (res) {
        if (res.code === 200) {
          _this.postForm = res.data;

          _this.$refs.summernote.setContent(_this.postForm.content);
        } else {
          _this.$alert(res.msg, '提示！', {
            confirmButtonText: '我知道啦！',
            type: 'warning'
          });

          _this.returnToList(0);
        }
      });
    }
  },
  methods: {
    uploadPIC: function uploadPIC(request) {
      var _this2 = this;

      getToken().then(function (data) {
        var formData = new FormData();
        formData.append('token', data.qiniu_token);
        formData.append('key', 'News/' + moment().format('x'));
        formData.append('file', request.file);
        uploadFile(formData).then(function (response) {
          _this2.postForm.pic = response.key;
        });
      });
    },
    exceed: function exceed() {
      this.$alert('请先删除已上传的照片！', '每个资讯标题图只能上传一张哦！', {
        confirmButtonText: '我知道啦！',
        type: 'warning'
      });
    },
    deletePic: function deletePic(file) {
      var _this3 = this;

      this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        deleteFile({
          key: file
        }).then(function (res) {
          if (res.code === 200 || res.code === 612) {
            _this3.$message({
              type: 'success',
              message: '删除成功!'
            });

            _this3.postForm.pic = '';
            return true;
          } else {
            _this3.$message({
              type: 'error',
              message: '删除失败!'
            });

            return false;
          }
        });
        return true;
      }).catch(function () {
        return false;
      });
      return false;
    },
    submitForm: function submitForm() {
      console.log(this.postForm.content);
      this.postForm.status = 1;
      this.submit();
    },
    draftForm: function draftForm() {
      this.postForm.status = 0;
      this.submit();
    },
    submit: function submit() {
      var _this4 = this;

      this.$refs.postForm.validate(function (valid) {
        if (valid) {
          _this4.loading = true;
          createNews(_this4.postForm).then(function (res) {
            _this4.loading = false;

            if (res.code === 200) {
              _this4.$notify({
                title: '成功',
                message: '资讯操作成功',
                type: 'success',
                duration: 2000
              });

              if (_this4.isEdit) {
                _this4.returnToList();

                return;
              }

              _this4.$refs['postForm'].resetFields();

              _this4.$refs.summernote.setContent('');

              _this4.postForm.update_time = moment().format('YYYY-MM-DD HH:mm:ss');
            }
          });
        }
      });
    },
    returnToList: function returnToList() {
      var _this5 = this;

      this.$store.dispatch('tagsView/delView', this.$router.history.current).then(function (_ref) {
        var visitedViews = _ref.visitedViews;
        visitedViews.slice(-1)[0];

        _this5.$router.push('/admin/pz/index');
      });
    }
  }
};
