import Pagination from '@/components/Pagination';
import { getNewsList, deleteNews } from '@/api/news';
import { cdnHost, newsCategories } from '@/api/utils';
export default {
  name: 'CaseList',
  components: {
    Pagination: Pagination
  },
  data: function data() {
    return {
      list: null,
      total: 0,
      listLoading: false,
      listQuery: {
        page: 1,
        limit: 20
      },
      cdnHost: cdnHost,
      newsCategories: newsCategories
    };
  },
  created: function created() {
    this.getList();
  },
  methods: {
    getList: function getList() {
      var _this = this;

      this.listLoading = true;
      this.list = [];
      getNewsList({
        type: 5
      }).then(function (res) {
        _this.listLoading = false;

        if (res.code === 200) {
          _this.list = res.data;
        }
      });
    },
    del: function del(info) {
      var _this2 = this;

      this.$confirm('即将删除图文' + info.title + ', 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        deleteNews({
          id: info.id
        }).then(function (res) {
          if (res.code === 200) {
            _this2.$message({
              type: 'success',
              message: '删除成功!'
            });
          }

          _this2.getList();
        });
      }).catch(function () {// 取消
      });
    }
  }
};
