import Swiper from "swiper";
import { getFrontNewsList } from "@/api/news";
import { cdnHost } from "@/api/utils";
export default {
  data: () => {
    return {
      list: [],
      cdnHost: cdnHost
    };
  },
  mounted() {
    getFrontNewsList({ type: 7 }).then(res => {
      if (res.code === 200) {
        this.list = res.data;
        this.$nextTick(() => {
          new Swiper(".swiper-banner", {
            slidesPerView: 1,
            loop: true,
            autoplay: true,
            pagination: {
              el: ".swiper-banner-pagination",
              clickable: true
            },
            navigation: {
              nextEl: ".swiper-banner-next",
              prevEl: ".swiper-banner-prev"
            }
          });
        });
      }
    });
  }
};
