import Pagination from '@/components/Pagination'
import { getNewsList, deleteNews } from '@/api/news'
import { cdnHost, newsCategories } from '@/api/utils'
export default {
  name: 'CaseList',
  components: { Pagination },
  data() {
    return {
      list: null,
      total: 0,
      listLoading: false,
      listQuery: {
        page: 1,
        limit: 20
      },
      cdnHost: cdnHost,
      newsCategories: newsCategories
    }
  },
  created() {
    this.getList()
  },
  methods: {
    getList() {
      this.listLoading = true
      this.list = []
      getNewsList({ }).then(res => {
        this.listLoading = false
        if (res.code === 200) {
          this.list = res.data
        }
      })
    },
    del(info) {
      this.$confirm('即将删除资讯' + info.title + ', 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        deleteNews({ id: info.id }).then(res => {
          if (res.code === 200) {
            this.$message({
              type: 'success',
              message: '删除成功!'
            })
          }
          this.getList()
        })
      }).catch(() => {
        // 取消
      })
    }
  }
}
