import Swiper from 'swiper'
import { cdnHost } from '@/api/utils'
import { getTeamIndexList } from '@/api/team'

export default {
    data() {
        return {
            data: [],
            cdnHost: cdnHost,
            navs: [],
            nav: null
        }
    },
    mounted() {
      let interval = setInterval(() => {
        if (!this.navs.length>0) {
          this.navs = this.$store.getters.navs;
        } else {
          this.navs.forEach(e=>{
            if(e.author == '/team'){
              this.nav = e
            }
          })
          clearInterval(interval);
        }
      }, 10);
      this.getList()
    },
    methods: {
        getList() {
            this.data = []
            getTeamIndexList().then(res => {
                if (res.code === 200) {
                    this.data = res.data
                    if(this.data.length>0){
                        this.$nextTick(() => {
                            new Swiper('.swiper-iTeam', {
                                slidesPerView: 5,
                                spaceBetween: 24,
                                centeredSlides: true,
                                loop: true,
                                autoplay: true,
                                pagination: {
                                    el: '.swiper-vr-pagination',
                                    clickable: true,
                                }
                            });
                        })
                    }
                }
            })
        }
    }
}
