import '@/assets/web/css/main.css'
import { getFrontNewsList } from '@/api/news'
export default {
  name: 'PageLayout',
  data: () => {
    return {
      list: [],
      path: '',
      pageHeight: 400
    }
  },
  computed: {
    key() {
      return this.$route.path
    }
  },
  created() {
    if(this._isMobile()){
      location.href='http://m.meiqiaozhuangshi.com'
    }
    if (!this.bindResize) {
      window.addEventListener("resize", this.calculateSize);
      this.bindResize = true;
    }
    const splitPath = this.$route.path.split('/')
    this.path = this.$route.path
    if (splitPath.length > 2) {
      this.path = '/' + splitPath[1]
    }
    getFrontNewsList({ type: 6 }).then(res => {
      if (res.code === 200) {
        this.list = res.data
        this.$store.dispatch('nav/storeNavs', this.list)
        this.list.forEach(e => {
          if (e.author === this.path) {
            this.$store.dispatch('nav/storeActiveNav', e)
          }
        })
        // console.log(this.$store.getters.navs)
      }
    })
  },
  mounted(){
    this.calculateSize()
  },
  methods:{
    _isMobile() {
      let flag = navigator.userAgent.match(/(phone|pad|pod|iPhone|iPod|ios|iPad|Android|Mobile|BlackBerry|IEMobile|MQQBrowser|JUC|Fennec|wOSBrowser|BrowserNG|WebOS|Symbian|Windows Phone)/i)
      return flag;
    },
    calculateSize(){
       this.pageHeight = window.innerHeight
    }
  }
}
