import { getInfo } from '@/api/news'
import { cdnHost } from '@/api/utils'
import moment from 'moment'
export default {
    data() {
        return {
            moment: moment,
            model: null,
            cdnHost: cdnHost,
            nav: null
        }
    },
    mounted() {
      let interval = setInterval(() => {
        if (!this.nav) {
          this.nav = this.$store.getters.activeNav;
        } else {
          clearInterval(interval);
        }
      }, 10)
      this.get()
    },
    methods: {
        get() {
            const id = this.$route.params && this.$route.params.id
            getInfo({id: id}).then(res => {
                if (res.code === 200) {
                    this.model = res.data
                }
            })
        }
    }
}
