import moment from 'moment'
import { createSubscribe,getSubscribeCount } from '@/api/subscribe'
export default {
  data(){
    return {
      count: 0,
      postForm: {
        name: '',
        phone: '',
        area: '',
        address: '',
        page: '首页',
        update_time: moment().format('YYYY-MM-DD HH:mm:ss'),
        status: 0
      }
    }
  },
  mounted() {
    this.getCount()
  },
  methods:{
    add(){
      if(!this.postForm.name||!this.postForm.phone||!this.postForm.area||!this.postForm.address){
        alert('请将信息填写完整！')
        return
      }
      if(this.postForm.phone.length!=11){
        alert('请填写正确的手机号码！')
        return
      }
      createSubscribe(this.postForm).then(res => {
                if (res.code === 200) {
                  alert('预约成功！')
                  this.getCount()
                  this.postForm= {
                      name: '',
                      phone: '',
                      area: '',
                      address: '',
                      page: '首页',
                      update_time: moment().format('YYYY-MM-DD HH:mm:ss'),
                      status: 0
                    }
                }
            })
    },
    getCount(){
      getSubscribeCount().then(res =>{
        console.log(res)
        if(res.code == 200){
           this.count = res.data
        }

      })
    }
  }
}
