import { cdnHost, newsCategories } from '@/api/utils'
import { getNewsIndexList } from '@/api/news'
import moment from 'moment'

export default {
    data() {
        return {
            data: [],
            cdnHost: cdnHost,
            newsCategories: newsCategories,
            activeIndex: 0,
            activeNewsId: 0,
            moment: moment,
            activeNews: [],
            navs: [],
            nav: null
        }
    },
    mounted() {
      let interval = setInterval(() => {
        if (!this.navs.length>0) {
          this.navs = this.$store.getters.navs;
        } else {
          this.navs.forEach(e=>{
            if(e.author == '/news'){
              this.nav = e
            }
          })
          clearInterval(interval);
        }
      }, 10);
      this.getList()

    },
    methods: {
        getList() {
            this.data = []
            getNewsIndexList({ }).then(res => {
                if (res.code === 200) {
                    this.data = res.data
                    if(this.data.length>0){
                        this.getCategoryNews(0)
                        this.$nextTick(()=>{

                        })
                    }
                }
            })
        },
        getCategoryNews(key){
            this.activeIndex=key
            this.activeNews = []
            this.data.forEach(element => {
                if(element.category === key){
                    if(this.activeNews.length === 0){
                        this.activeNewsId = element.id
                    }
                    this.activeNews.push(element)
                }
            })
        }
    }
}
