import Swiper from 'swiper'
import { designStyles, cdnHost, homeOptions } from '@/api/utils'
import { getPanoIndexList } from '@/api/pano'
export default {
    data() {
        return {
            designStyles: designStyles,
            data: [],
            cdnHost: cdnHost
        }
    },
    mounted() {
        this.getList()

    },
    methods: {
        getList() {
            this.data = []
            getPanoIndexList().then(res => {
                if (res.code === 200) {
                    this.data = res.data
                    if(this.data.length>0){
                        this.$nextTick(() => {
                            new Swiper('.swiper-vr', {
                                slidesPerView: 1,
                                loop: true,
                                autoplay: true,
                                navigation: {
                                    nextEl: '.swiper-vr-next',
                                    prevEl: '.swiper-vr-prev',
                                },
                            });
                        })
                    }
                }
            })
        },
        getType(value) {
            let type = ''
            homeOptions.forEach(element => {
                if (element.value === value) {
                    type = element.label
                }
            })
            return type
        },
        getDesignStyle(value) {
            let style = ''
            designStyles.forEach(element => {
                if (element.value === value) {
                    style = element.label
                }
            })
            return style
        }
    }
}
