import { getWorksitePage } from '@/api/case'
import {
  designStyles,
  cdnHost,
  homeOptions,
  workSiteAlbumTypes
} from '@/api/utils'
import moment from 'moment'
import { createSubscribe, getSubscribeCount } from '@/api/subscribe'
export default {
  data() {
    return {
      page: 1,
      pages: 0,
      total: 0,
      has_next: false,
      has_prev: false,
      data: [],
      cdnHost: cdnHost,
      designStyles: designStyles,
      homeOptions: homeOptions,
      workSiteAlbumTypes: workSiteAlbumTypes,
      nav: null,
      caseStatus: -1,
      keyword: '',
      bouncedUser: '-1',
      count: 0,
      postForm: {
        name: '',
        phone: '',
        area: '',
        address: '',
        page: '',
        update_time: moment().format('YYYY-MM-DD HH:mm:ss'),
        status: 0
      }
    }
  },
  mounted() {
    this.getCount()
    const interval = setInterval(() => {
      if (!this.nav) {
        this.nav = this.$store.getters.activeNav
      } else {
        clearInterval(interval)
      }
    }, 10)
    this.getList(1)
  },
  methods: {
    changeCaseStatus(caseStatus){
      this.caseStatus = caseStatus
      this.getList(1)
    },
    getList(page) {
      this.data = []
      getWorksitePage(page,{case_status: this.caseStatus, keyword: this.keyword}).then(res => {
        if (res.code === 200) {
          this.data = res.data
          this.pages = res.pages
          this.page = res.page
          this.has_next = res.has_next
          this.has_prev = res.has_prev
          this.total = res.total
        }
      })
    },
    getType(value) {
      let type = ''
      homeOptions.forEach(element => {
        if (element.value === value) {
          type = element.label
        }
      })
      return type
    },
    getDesignStyle(value) {
      let style = ''
      designStyles.forEach(element => {
        if (element.value === value) {
          style = element.label
        }
      })
      return style
    },
    bouncedOpen(name){
      this.postForm = {
        name: '',
        phone: '',
        area: '',
        address: '',
        page: name,
        update_time: moment().format('YYYY-MM-DD HH:mm:ss'),
        status: 0
      }
      this.bouncedUser = name
    },
     add(){
      if(!this.postForm.name||!this.postForm.phone||!this.postForm.address){
        alert('请将信息填写完整！')
        return
      }
      if(this.postForm.phone.length!=11){
        alert('请填写正确的手机号码！')
        return
      }
      createSubscribe(this.postForm).then(res => {
                if (res.code === 200) {
                  alert('预约成功！')
                  this.getCount()
                  this.bouncedUser = '-1'
                }
            })
    },
    getCount(){
      getSubscribeCount().then(res =>{
        console.log(res)
        if(res.code == 200){
           this.count = res.data
        }

      })
    }
  }
}
