import { getCasePage } from "@/api/case";
import { getPanoPage } from "@/api/pano";
import { designStyles, cdnHost, homeOptions } from "@/api/utils";
export default {
  data() {
    return {
      page: 1,
      pages: 0,
      total: 0,
      has_next: false,
      has_prev: false,
      data: [],
      cdnHost: cdnHost,
      designStyles: designStyles,
      homeOptions: homeOptions,
      nav: null,
      activeIndex: 0,
      typeId: -1,
      designId: -1,
      areaId: -1,
      keyword: ''
    };
  },
  mounted() {
    this.designId=parseInt(this.$route.query.designId)
    if(isNaN(this.designId)){
      this.designId = -1
    }
    let interval = setInterval(() => {
      if (!this.nav) {
        this.nav = this.$store.getters.activeNav;
      } else {
        clearInterval(interval);
      }
    }, 10);
    this.getList(1);
  },
  methods: {
    getList(page) {
      this.data = [];
      if(this.activeIndex == 0){
        getCasePage(page,{ typeId: this.typeId, designId: this.designId, areaId: this.areaId, keyword: this.keyword }).then(res => {
          if (res.code === 200) {
            this.data = res.data;
            this.pages = res.pages;
            this.page = res.page;
            this.has_next = res.has_next;
            this.has_prev = res.has_prev;
            this.total = res.total;
          }
        });
      }else{
        getPanoPage(page,{ typeId: this.typeId, designId: this.designId, areaId: this.areaId, keyword: this.keyword })
        .then(res => {
              if (res.code === 200) {
                this.data = res.data;
                this.pages = res.pages;
                this.page = res.page;
                this.has_next = res.has_next;
                this.has_prev = res.has_prev;
                this.total = res.total;
              }
            })
          }
    },
    getType(value) {
      let type = "";
      homeOptions.forEach(element => {
        if (element.value === value) {
          type = element.label;
        }
      });
      return type;
    },
    getDesignStyle(value) {
      let style = "";
      designStyles.forEach(element => {
        if (element.value === value) {
          style = element.label;
        }
      });
      return style;
    },
    changeType(typeId){
      this.typeId = typeId
      this.getList(1)
    },
    changeDesign(designId){
      this.designId = designId
      this.getList(1)
    },
    changeArea(areaId){
      this.areaId = areaId
      this.getList(1)
    },
    changeShowCase(activeId){
      this.activeIndex = activeId
      this.getList(1)
    },
  }
};
