import { qiniuHost, cdnHost, typeOptions } from '@/api/utils'
import { getToken, uploadFile, deleteFile } from '@/api/qiniu'
import { createTeam, getTeam } from '@/api/team'
import moment from 'moment'
export default {
  props: {
    isEdit: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      postForm: {
        name: '',
        job: '',
        pic: '',
        type: '',
        year: 5,
        description: '',
        view_count: 0,
        update_time: moment().format('YYYY-MM-DD HH:mm:ss')
      },
      rules: {
        name: [
          { required: true, message: '请填写成员名称', trigger: 'blur' }
        ],
        job: [
          { required: true, message: '请填写职位', trigger: 'blur' }
        ],
        pic: [
          { required: true, message: '请上传职业照', trigger: 'blur' }
        ],
        type: [
          { required: true, message: '请选择类别', trigger: 'blur' }
        ],
        year: [
          { required: true, message: '请填写工作年限', trigger: 'blur' }
        ],
        description: [
          { required: true, message: '请填写简介', trigger: 'blur' }
        ],
        view_count: [
          { required: true, message: '阅读数', trigger: 'blur' }
        ],
        update_time: [
          { required: true, message: '请选择更新时间', trigger: 'blur' }
        ]
      },
      typeOptions: typeOptions,
      qiniuHost: qiniuHost,
      preview_image: '',
      cdnHost: cdnHost,
      loading: false
    }
  },
  created() {
    if (this.isEdit) {
      const id = this.$route.params && this.$route.params.id
      getTeam({ id: id }).then(res => {
        if (res.code === 200) {
          this.postForm = res.data
        } else {
          this.$alert('请先删除已上传的照片，然后再上传新的职业照哦！', '每个成员的职业照只能上传一张哦！', {
            confirmButtonText: '我知道啦！',
            type: 'warning'
          })
        }
      })
    }
  },
  mounted() {
  },
  methods: {
    uploadTeamHeader(request) {
        getToken().then(data => {
          var formData = new FormData()
          formData.append('token', data.qiniu_token)
          formData.append('key', 'Team/'+moment().format('x'))
          formData.append('file', request.file)
          uploadFile(formData).then(response => {
            this.postForm.pic = response.key
          })
        })
    },
    exceed() {
      this.$alert('请先删除已上传的照片，然后再上传新的职业照哦！', '每个成员的职业照只能上传一张哦！', {
        confirmButtonText: '我知道啦！',
        type: 'warning'
      })
    },
    deletePic(file) {
      this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        deleteFile({ key: file }).then((res) => {
          if (res.code === 200  || res.code === 612) {
            this.$message({
              type: 'success',
              message: '删除成功!'
            })
            this.postForm.pic = ''
            return true
          } else {
            this.$message({
              type: 'error',
              message: '删除失败!'
            })
            return false
          }
        })
        return true
      }).catch(() => {
        return false
      })
      return false
    },
    submitForm() {
      this.postForm.status = 1
      this.submit()
    },
    draftForm() {
      this.postForm.status = 0
      this.submit()
    },
    submit() {
      this.$refs.postForm.validate(valid => {
        if (valid) {
          this.loading = true
          createTeam(this.postForm).then((res) => {
            this.loading = false
            if (res.code === 200) {
              this.$notify({
                title: '成功',
                message: '团队成员操作成功',
                type: 'success',
                duration: 2000
              })
              this.loading = false
              this.$refs['postForm'].resetFields()
              if (this.isEdit) {
                this.returnToList()
              }
            }
          })
        }
      })
    },
    returnToList() {
      this.$store.dispatch('tagsView/delView', this.$router.history.current).then(({ visitedViews }) => {
        visitedViews.slice(-1)[0]
        this.$router.push('/admin/team/list')
      })
    }
  }
}
