import Sticky from '@/components/Sticky'
import Swiper from 'swiper'
import { getInfo } from '@/api/case'
import { designStyles, cdnHost, homeOptions, caseAlbumTypes } from '@/api/utils'
export default {
    components: { Sticky },
    data() {
        return {
            model: null,
            cdnHost: cdnHost,
            caseAlbumTypes: caseAlbumTypes,
            nav: null,
        }
    },
    mounted() {
      let interval = setInterval(() => {
      if (!this.nav) {
        this.nav = this.$store.getters.activeNav;
      } else {
        clearInterval(interval);
      }
    }, 10);
        this.get()
    },
    methods: {
        get() {
            const id = this.$route.params && this.$route.params.id
            getInfo({id: id}).then(res => {
                if (res.code === 200) {
                    this.model = res.data
                    this.model.albums={}
                    if (res.data.album && res.data.album.length > 0) {
                        res.data.album.forEach(element => {
                        if (!this.model.albums[element.type] || this.model.albums[element.type].length === 0) {
                            this.$set(this.model.albums, element.type, [])
                        }
                        this.model.albums[element.type].push({ name: element.url, url: cdnHost + element.url })
                        })
                    }
                    this.$nextTick(()=>{
                        new Swiper('.swiper-nCaseShow', {
                            slidesPerView: 1,
                            loop: true,
                            autoplay: true,
                        });
                    })

                }
            })
        },
        getType(value) {
            let type = ''
            homeOptions.forEach(element => {
                if (element.value === value) {
                    type = element.label
                }
            })
            return type
        },
        getDesignStyle(value) {
            let style = ''
            designStyles.forEach(element => {
                if (element.value === value) {
                    style = element.label
                }
            })
            return style
        }
    },
}
