import { getNewsPage } from "@/api/news";
import {
  designStyles,
  cdnHost,
  homeOptions,
  newsCategories
} from "@/api/utils";
import moment from "moment";
export default {
  data() {
    return {
      page: 1,
      pages: 0,
      total: 0,
      has_next: false,
      has_prev: false,
      data: [],
      cdnHost: cdnHost,
      designStyles: designStyles,
      homeOptions: homeOptions,
      newsCategories: newsCategories,
      moment: moment,
      nav: null,
      activeIndex: 0
    };
  },
  mounted() {
    let interval = setInterval(() => {
      if (!this.nav) {
        this.nav = this.$store.getters.activeNav;
      } else {
        clearInterval(interval);
      }
    }, 10);
    this.getList(1);
  },
  methods: {
    getList(page) {
      this.data = [];
      getNewsPage(page, { type: 3 }).then(res => {
        if (res.code === 200) {
          this.data = res.data;
          this.pages = res.pages;
          this.page = res.page;
          this.has_next = res.has_next;
          this.has_prev = res.has_prev;
          this.total = res.total;
        }
      });
    },
    getType(value) {
      let type = "";
      homeOptions.forEach(element => {
        if (element.value === value) {
          type = element.label;
        }
      });
      return type;
    },
    getDesignStyle(value) {
      let style = "";
      designStyles.forEach(element => {
        if (element.value === value) {
          style = element.label;
        }
      });
      return style;
    }
  }
};
